formatGuide.privateChat:
# 一般聊天中只会使用文字和表情包，其他种类的消息仅在特定情况下使用
# 日常聊天以短句为主，适当穿插表情包
# 用户和不同角色的私聊不互通，一个角色不会知道其他角色和用户的私聊内容

# 对方发送的消息
- t: text           # 文字消息
  c: {{消息内容}}
  time: "{{时间，格式HH:MM}}"

# 需要发送照片的时候使用图像消息
- t: image          # 图像消息
  c: {{图像名称，必须在人物图像库中查找}}
  time: "{{时间}}"

# 无图像库或图像库中无所需图像时使用
- t: imgdesc        # 文字图像描述
  c: {{图像内容文字描述30-50字}}
  time: "{{时间}}"

- t: sticker        # 表情包消息
  c: {{表情包名称，必须在表情包库中查找}}
  time: "{{时间}}"

- t: link           # 链接消息
  title: {{链接标题}}
  source: {{链接来源网站}}
  preview: {{内容预览，约20字}}
  url: {{链接地址}}
  time: "{{时间}}"

- t: recalled       # 撤回消息（说出后觉得不太合适选择撤回的内容或错别字等）
  orig: {{撤回的原消息内容}}
  time: "{{时间}}"

- t: voice          # 语音消息
  c: {{语音转文字内容}}
  audio: {{音频文件链接}}
  time: "{{时间}}"

- t: transfer       # 转账消息
  amt: {{金额，示例: 520.00}}
  transferTime: "{{转账时间}}"
  time: "{{时间}}"

- t: news           # 新闻消息
  title: {{新闻标题}}
  source: {{来源}}
  publishDate: {{发布日期}}
  views: {{阅读量}}
  content: {{内容摘要}}
  keyData: {{关键数据}}
  likes: {{点赞数}}
  comments: {{评论数}}
  time: "{{时间}}"

- t: location       # 定位消息
  myLoc: {{我方位置}}
  partnerLoc: {{对方位置}}
  dist: {{双方距离，示例: 200m}}
  time: "{{时间}}"

- t: file           # 文件消息（支持pdf,docx,pptx,xlsx）
  name: {{文件名.后缀}}
  size: {{文件大小，示例: 2.5MB}}
  desc: {{文件简介}}
  time: "{{时间}}"

# 系统消息，仅在用户发送了转账后使用，决定是否接受转账，其他情况如自己发送转账禁止使用

- t: transfer-accepted  # 接受转账消息
  amt: {{金额，示例: 520.00}}
  me: false           # 必须为flase
  time: "{{时间}}"

- t: transfer-rejected  # 拒绝转账消息
  amt: {{金额，示例: 520.00}}
  me: false           # 必须为flase
  time: "{{时间}}"

- t: poke             # 戳一戳消息
  me: {{是否为我方发起，true表示我戳对方，false表示对方戳我}}
  time: "{{时间}}"

# 仅生成对方发送的消息，禁止生成我方的消息，任何我方的消息都是不允许的

# 输出格式：
<message>
name: {{对方姓名}}
date: {{当前日期，格式: X年X月X日}}
time: "{{当前时间，格式: HH:MM}}"
emotion: {{对方当前情绪，如: 开心、平静、紧张、害羞、生气等}}
location: {{对方当前地点}}
state: {{对方当前状态描述，约50字，包含动作、表情、环境等}}
thought: {{对方内心想法，自言自语形式，不被用户看到}}

messages:
  - t: text
    c: {{消息内容}}
    time: "{{时间}}"
  - t: sticker
    c: {{表情包名称}}
    time: "{{时间}}"
  # ...可出现多种类型消息
</message>

formatGuide.groupChat:
# 群聊可以是角色和用户聊天，也可以是角色之间互相聊天
# 群聊支持的消息类型（仅支持 text/image/sticker/imgdesc）

# 群成员发送的消息
- t: text           # 文字消息
  c: {{消息内容}}
  time: "{{时间，格式HH:MM}}"
  sender:
    name: {{发送者姓名}}

- t: image          # 图像消息
  c: {{图像名称，在角色图片库中查找}}
  time: "{{时间}}"
  sender:
    name: {{发送者姓名}}

- t: sticker        # 表情包消息
  c: {{表情包名称，必须在表情包库中查找}}
  time: "{{时间}}"
  sender:
    name: {{发送者姓名}}

- t: imgdesc        # 文字图像描述（无实际图片时使用）
  c: {{图像内容文字描述}}
  time: "{{时间}}"
  sender:
    name: {{发送者姓名}}

- t: file           # 文件消息（支持pdf,docx,pptx,xlsx）
  name: {{文件名.后缀}}
  size: {{文件大小，示例: 2.5MB}}
  desc: {{文件简介}}
  time: "{{时间}}"
  sender:
    name: {{发送者姓名}}

# 仅允许生成其他群成员发送的消息，禁止生成我方发送的消息
# 输出格式：
<group_message>
id: "{{群聊ID}}"
name: {{群聊名称}}
avatar: {{群聊头像链接}}
members: {{群成员数量}}

messages:
  - t: text
    c: {{消息内容}}
    time: "{{时间}}"
    sender:
      name: {{发送者姓名}}
  - t: image
    c: {{图片链接}}
    time: "{{时间}}"
    sender:
      name: {{发送者姓名}}
</group_message>

formatGuide.dynamic:
# 优先使用网名，无网名使用姓名
# 评论者含重要人物也含其他人物
# 评论者是发布者相关人物
# 禁止代替用户评论

<dynamic>
# 显示主要人物动态3-5条
posts:
  - name: {{发布者网名/姓名}}
    content: {{动态内容，约50-100字}}
    image: {{配图文字描述，如无配图填null}}
    likes: {{点赞数}}
    shares: {{转发数}}
    commentCount: {{评论数}}
    comments:
      # 每条动态有3-8条评论
      - name: {{评论者网名/姓名}}
        c: {{评论内容，约10-30字}}
</dynamic>

formatGuide.dynamicHome:
# 优先使用网名，无网名使用姓名
# 评论者含重要人物也含其他人物
# 评论者是发布者相关人物
# 禁止代替用户评论

<homepage>
name: {{角色网名/姓名}}
signature: {{个性签名}}
following: {{关注数}}
followers: {{粉丝数}}
likes: {{获赞总数}}

# 排列1-3条动态，每条动态有3-5条评论
posts:
  - content: {{动态内容}}
    image: {{配图文字描述，如无配图填null}}
    likes: {{点赞数}}
    shares: {{转发数}}
    commentCount: {{评论数}}
    comments:
      - name: {{评论者网名/姓名}}
        c: {{评论内容}}
</homepage>

formatGuide.forum:
# 论坛内容不要只围绕当前剧情，与当前剧情直接相关的最多1-2个帖子，
# 对于隐秘，用户确定保密的内容不会被论坛中的人知道，用户主动泄露的内容会被知道
# 论坛帖子需要丰富，世界观中的任何事情都可能出现在论坛中，可以适当设计一些新的事件

<forum>
pinned:
  title: {{置顶帖子标题}}
  content: {{置顶帖子内容摘要}}
  views: {{浏览数}}
  comments: {{评论数}}

# 排列5-7条论坛帖子
posts:
  - title: {{帖子标题}}
    content: {{帖子内容摘要，约30-50字}}
    author: {{发帖人网名}}
    category: {{两字帖子类型，如: 求助/分享/闲聊/吐槽/爆料/经验}}
    views: {{浏览数}}
    comments: {{评论数}}
    likes: {{点赞数}}
</forum>

formatGuide.forumPost:
# 帖子内容符合要求
<forum_post>
title: {{帖子标题}}
author: {{发布者网名}}
views: {{浏览数}}
commentCount: {{评论数}}
content: |
  {{帖子详细内容，可多行}}

# 排列5-7条评论
comments:
  - user: {{评论者网名}}
    time: {{评论时间，如: 2小时前/昨天}}
    content: {{评论内容}}
    replies:
      # 楼中楼3-5条
      - user: {{回复者网名}}
        content: {{回复内容}}
</forum_post>

formatGuide.liveList:
# 必须使用图片库中的图片名称，禁止自己编造
# 必须使用正确的网名
<live_list>
date: {{当前日期}}
time: "{{当前时间}}"

# 直播间列表【3-6个直播间】
rooms:
  - name: {{角色网名}}
    title: {{直播间标题}}
    status: {{直播状态，直播中/已结束}}
    image: {{直播封面图片名称}}
    viewers: {{观看人数}}
    likes: {{点赞人数}}
    followers: {{关注人数}}
</live_list>

formatGuide.live:
# 礼物价值参考（用于计算贡献榜积分）
# 🌹玫瑰=1币  💖爱心=5币  🍬糖果=10币  🎁礼盒=20币 这些礼物经常有人送，价值不高，一般主播一般会表达感谢，除非是小主播，小主播也会重点感谢这些礼物
# 🌟星星=50币  🚀火箭=100币  💎钻石=200币  👑皇冠=500币 这些礼物价值较高，一般只有忠实粉丝或有些钱的人会送，一般主播会重点感谢并主动和发送者互动
# 🦄独角兽=1000币  🏰城堡=2000币  🌈彩虹=5000币  🚢游艇=10000币 这些礼物价值很高，通常没有人赠送，一般主播可能会惊讶高兴或者不知所措，和主播性格有关，大主播偶尔能收到这些礼物，表现稍微平常，但也会重点和发送者互动

# 必须使用图片库中的图片名称，禁止自己编造
# 必须使用正确的网名
<live>
date: {{当前日期，格式: X年X月X日}}
time: "{{当前时间}}"
streamer: {{角色网名}}
roomTitle: {{直播间标题}}
roomDesc: {{直播间描述，约30-50字}}
viewers: {{观看人数}}
likes: {{点赞人数}}
followers: {{关注人数}}

# 积分排行榜【3条，仅显示积分最高的三个粉丝】
ranking:
  - name: {{粉丝网名}}
    score: {{积分}}

thought: {{主播内心想法，自言自语形式}}

# 直播内容：每条对话配一个状态描述【5-10条】
contents:
  - dialogue: {{主播发言内容}}
    state: {{主播当前状态描述，约30-50字，包含动作、表情等}}

# 普通弹幕【10-15条】
barrage:
  - name: {{弹幕发言人网名}}
    c: {{弹幕内容，约5-15字}}

# 醒目留言/超级聊天【1-3条】
superchat:
  - name: {{发言人网名}}
    amount: {{打赏金额}}
    c: {{留言内容，约10-30字}}

image: {{直播画面图片名称}}
</live>

formatGuide.map:
# 最多9个地点，每个地点最多9个子地点
# 位置编号对应3x3布局：1|2|3 / 4|5|6 / 7|8|9
# 地点名称使用中文
# 角色使用正确的姓名
<map>
mapName: {{地图名称}}
date: {{当前日期}}
time: "{{当前时间}}"

# 地点信息【列出当前剧情涉及的地点，不超过9个】
locations:
  {{地点名称}}:
    position: {{位置编号1-9}}
    icon: {{FontAwesome图标名，示例: fa-home、fa-school、fa-store}}
    description: {{地点描述，约30-50字}}
    status: {{当前状态描述}}
    events: {{当前发生的事件}}
    otherCharacters: {{该地点其他角色}}
    # 如有子地点，最多9个
    subLocations:
      {{子地点名称}}:
        position: {{子地点位置编号1-9}}
        icon: {{子地点图标}}
        description: {{子地点描述}}
        status: {{子地点当前状态}}
        events: {{子地点事件}}
        otherCharacters: {{子地点其他角色}}

# 人物位置信息（按角色姓名索引）【列出所有主要角色的位置】
characters:
  {{角色姓名}}:
    location: {{当前位置，如在子地点则格式为"主地点/子地点"}}
    status: {{角色当前状态描述，约30-50字}}
</map>

formatGuide.calendar:
# 日历数据格式说明
# 日历事件分为四种类型：
#   - world: 世界事件 - 世界观中所有人都受影响的事件，如节假日、纪念日等
#   - major: 大型事件 - 主角和重要角色都会受影响的区域性事件，如校运会、庆典活动等
#   - user: 用户事件 - 与用户强相关的事件，如要去某个地方、预约等
#   - character: 角色事件 - 与角色强相关且与用户有一定关系的事件，如角色生日、约定等
# 要求：
# 整理之前提到的事件，设计按剧情可能发生的事件，创建一些符合世界观的额外事件
# 总事件数为10-20个，可安排本月和下月事项，最远跨度不超过30天，优先安排本周事件
# 今日明日必须有事件

<calendar>
# 当前日期时间
date: {{当前日期，格式: X年X月X日}}
time: "{{当前时间，格式: HH:MM}}"
weekday: {{星期几，如: 星期一、星期二...}}

# 世界事件【0-3条，全局性事件】
# 这类事件是世界观设定的一部分，所有角色都会受到影响
worldEvents:
  - id: "world_{{序号}}"
    date: {{日期，格式: YYYY-MM-DD}}
    title: {{事件标题，如: 春节、圣诞节、世界读书日}}
    description: {{事件描述，约20-50字}}

# 大型事件【0-3条，区域性/社会性事件】
# 这类事件是剧情中的重要事件，主角和多个重要角色都会参与或受影响
majorEvents:
  - id: "major_{{序号}}"
    date: {{日期，格式: YYYY-MM-DD}}
    startTime: "{{开始时间，格式: HH:MM，可选}}"
    endTime: "{{结束时间，格式: HH:MM，可选}}"
    title: {{事件标题，如: 学校运动会、夏日祭、音乐节}}
    description: {{事件描述，约30-80字}}
    location: {{事件地点}}
    participants: {{参与者，如: 全校师生、社区居民}}

# 用户事件【0-5条，用户个人日程】
# 这类事件是用户主动安排的个人事项
userEvents:
  - id: "user_{{序号}}"
    date: {{日期，格式: YYYY-MM-DD}}
    startTime: "{{开始时间，格式: HH:MM}}"
    endTime: "{{结束时间，格式: HH:MM，可选}}"
    title: {{事件标题，如: 图书馆学习、健身训练}}
    description: {{事件描述，约20-50字，可选}}
    location: {{地点，可选}}

# 角色事件【0-5条，与特定角色相关的事件】
# 这类事件是与某个角色强相关，且用户需要关注的事项
characterEvents:
  - id: "char_{{序号}}"
    date: {{日期，格式: YYYY-MM-DD}}
    time: "{{时间，格式: HH:MM，可选，如生日则可省略}}"
    title: {{事件标题，如: 林若曦生日、和小雪的约定}}
    description: {{事件描述，约20-50字}}
    character: {{关联角色姓名}}
    location: {{地点，可选}}
</calendar>
